CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_TauxAvancementByEtabAnneF`(etab varchar(20),ann varchar(10),taux_etab double)
begin
	declare t_avc,som_N,som_O double default 0;
    select sum(avc) into t_avc  from affectmodule  e inner join Groupe g on e.Groupe = g.CodeGrp  where e.AnneeFr = ann and e.CodeEtab = etab;
    
    select (sum(m.s1) + sum(m.s2)) * taux_etab into som_O  from Groupe g  
    inner join filiere f on f.CodeFlr = g.CodeFlr 
    inner join modules m on f.CodeFlr = m.CodeFlr
    where g.CodeEtab = etab and m.Annee = g.Annee and g.FPA = "O" 
    and g.Codegrp in (select distinct groupe  from affectmodule  e inner join Groupe g on e.Groupe = g.CodeGrp  where e.AnneeFr = ann and e.CodeEtab = etab);
	
    select sum(m.s1) + sum(m.s2) into som_N  from Groupe g  
    inner join filiere f on f.CodeFlr = g.CodeFlr 
    inner join modules m on f.CodeFlr = m.CodeFlr
    where g.CodeEtab = etab and m.Annee = g.Annee and g.FPA = "N"
    and g.Codegrp in (select distinct groupe  from affectmodule  e inner join Groupe g on e.Groupe = g.CodeGrp  where e.AnneeFr = ann and e.CodeEtab = etab);
    
	if som_N is null then
		set som_N = 0;
    end if;
    if som_O is null then
		set som_O = 0;
    end if;
    select (t_avc / (som_N + som_O)) *100 as taux;
 end ;;
