CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_taux_Avencement_Module`(taux double,filtre varchar(10),anne varchar(10),etab varchar(10))
begin
	declare n int default 0;
    declare nb_affct int default 0;
	declare mat,nom,comodule,codegrp varchar(60);
    declare descpmodule varchar(400);
    declare avancement double;
    
    declare cur_affec_emploi_O cursor for 
    select distinct e.matricule,concat(f.nom," ",f.prenom),e.Codemodule,m.DescpMd,e.CodeGrp,af.avc / ((m.s1 + m.s2) * 0.6) * 100 as avancement
	from formateur f,emploireel e ,affectmodule af , modules m ,Groupe g
	where e.Matricule = af.Matricule and e.CodeModule = af.ModuleCode and e.CodeGrp = af.Groupe
	and af.ModuleCode = m.CodeMd and af.Groupe = g.CodeGrp and g.CodeFlr = m.CodeFlr 
    and e.Matricule = f.Matricule and (af.avc / ((m.s1 + m.s2) * 0.6) * 100) >= taux
    and e.CodeEtb = etab and e.AnneeFr = anne and  g.FPA = "O"
	order by e.CodeGrp ;
    
   declare cur_affec_emploi_N cursor for 
    select distinct e.matricule,concat(f.nom," ",f.prenom),e.Codemodule,m.DescpMd,e.CodeGrp,(af.avc/(m.s1 + m.s2))*100 as avancement
	from formateur f,emploireel e ,affectmodule af , modules m ,Groupe g
	where e.Matricule = af.Matricule and e.CodeModule = af.ModuleCode and e.CodeGrp = af.Groupe
	and af.ModuleCode = m.CodeMd and af.Groupe = g.CodeGrp and g.CodeFlr = m.CodeFlr 
    and e.Matricule = f.Matricule and (af.avc/(m.s1 + m.s2))*100 >= taux
    and e.CodeEtb = etab and e.AnneeFr = anne and  g.FPA = "N"
	order by e.CodeGrp ;
    
    
 
    
    declare Continue handler for Not found set n = 1;
    
    create table taux_affect(
    matricule varchar(100),
    nom varchar(100),
    codemodule varchar(100),
    descptionmodule varchar(400),
    codegroupe varchar(100),
    avancement varchar(100),
    message varchar(100)
    );
    
    
    open cur_affec_emploi_O;
		repete:loop
			fetch cur_affec_emploi_O into mat,nom,comodule,descpmodule,codegrp,avancement;
            if n = 1 then
				leave repete;
            end if;
            set nb_affct = Nb_Affectation(mat,codegrp,comodule);
            if nb_affct = 0 then
				insert into taux_affect values(mat,nom,comodule,descpmodule,codegrp,avancement,'groupe');
			else
				insert into taux_affect values(mat,nom,comodule,descpmodule,codegrp,avancement,'module');
            end if;
		end loop repete;
    Close cur_affec_emploi_O;
    
    set n = 0;
    open cur_affec_emploi_N;
		repete:loop
			fetch cur_affec_emploi_N into mat,nom,comodule,descpmodule,codegrp,avancement;
            if n = 1 then
				leave repete;
            end if;
            set nb_affct = Nb_Affectation(mat,codegrp,comodule);
            if nb_affct = 0 then
				insert into taux_affect values(mat,nom,comodule,descpmodule,codegrp,avancement,'groupe');
			else
				insert into taux_affect values(mat,nom,comodule,descpmodule,codegrp,avancement,'module');
            end if;
		end loop repete;
    Close cur_affec_emploi_N;
    
	if filtre = "tout" then
		select * from taux_affect order by codegroupe;
    else
		select * from taux_affect where message = filtre order by codegroupe;
    end if;
	drop table taux_affect;
 end ;;
